#include <stdio.h>

int main() {
    float media;

    printf("Digite a media do aluno: ");
    scanf("%f", &media);

    if (media >= 7.0) {
        printf("Situacao: APROVADO.\n");
    } else if (media >= 5.0) { // So e testado se o primeiro "if" for falso
        printf("Situacao: RECUPERACAO.\n");
    } else { // Executado se nenhuma das condicoes anteriores for verdadeira
        printf("Situacao: REPROVADO.\n");
    }

    return 0;
}
