int contador = 10;
contador += 3;    // equivale a contador = contador + 3;
contador -= 1;    // decrementa mantendo o histórico
contador++;        // incrementa de 1 em 1
--contador;        // pré-decremento antes de usar o valor

float n1, n2, n3;
float media;

printf("Digite as tres notas: ");
scanf("%f %f %f", &n1, &n2, &n3);
media = (n1 * 4 + n2 * 3 + n3 * 3) / 10.0f;
if (media >= 7.0f && media <= 10.0f) {
  printf("Aprovado com media %.1f\n", media);
} else if (media >= 5.0f) {
  printf("Recuperacao com media %.1f\n", media);
} else {
  printf("Reprovado com media %.1f\n", media);
}
