float salarioBase, gratificacao, imposto, salarioLiquido;
printf("Salario base: ");
scanf("%f", &salarioBase);
gratificacao = salarioBase * 0.05f;
imposto = salarioBase * 0.07f;
salarioLiquido = salarioBase + gratificacao - imposto;
printf("Liquido: R$ %8.2f\n", salarioLiquido);

float peso, altura, imc;
printf("Peso (kg): ");
scanf("%f", &peso);
printf("Altura (m): ");
scanf("%f", &altura);
imc = peso / (altura * altura);
printf("IMC = %.2f\n", imc);
if (imc < 18.5f) {
  printf("Classificacao: Abaixo do peso\n");
} else if (imc < 25.0f) {
  printf("Classificacao: Peso adequado\n");
} else if (imc < 30.0f) {
  printf("Classificacao: Sobrepeso\n");
} else {
  printf("Classificacao: Obesidade\n");
}
