switch (opcao) {
  case 1:
    printf("Cadastrar");
    break;
  case 2:
    printf("Listar");
    break;
  default:
    printf("Opcao invalida");
    break;
}

// Linha 12: valida entrada e evita opções inválidas
// Linha 18: agrupa cases 2 e 3 para reutilizar mensagem
// Linha 26: default orienta o usuário a repetir a escolha
// TODO Linha 30: considerar adicionar confirmação antes de sair

switch (mes) {
  case 1: case 3: case 5: case 7: case 8: case 10: case 12:
    printf("Mes com 31 dias");
    break;
  case 4: case 6: case 9: case 11:
    printf("Mes com 30 dias");
    break;
  case 2:
    printf("Fevereiro: 28 ou 29 dias");
    break;
  default:
    printf("Valor invalido");
    break;
}
