#include <stdio.h>
#include <stdlib.h>
#include <time.h>

const char *classificar(float temperatura, int tosse, int saturacao) {
  if (temperatura >= 39.0f || saturacao < 92) {
    return "EMERGENCIA";
  } else if ((temperatura >= 37.8f && tosse) || saturacao < 95) {
    return "URGENCIA";
  } else if (temperatura >= 37.0f) {
    return "OBSERVACAO";
  }
  return "AMBULATORIO";
}

int main(void) {
  srand((unsigned)time(NULL));

  float temperatura = (float)(rand() % 71 + 350) / 10.0f;
  int tosse = rand() % 2;
  int saturacao = rand() % 10 + 90;

  printf("Entrada (temperatura tosse saturacao):\n%.1f %d %d\n", temperatura, tosse, saturacao);
  printf("Saida esperada: %s\n", classificar(temperatura, tosse, saturacao));

  return 0;
}
