#include <stdio.h>
#include <stdlib.h>
#include <time.h>

const char *classificarCredito(int score, float renda, float divida) {
  float comprometimento = divida / renda;

  if (score >= 800 && comprometimento <= 0.2f) {
    return "LIMITE MAXIMO";
  } else if (score >= 650 && comprometimento <= 0.35f) {
    return "LIMITE MEDIO";
  } else if (score >= 500 && comprometimento <= 0.45f) {
    return "LIMITE BAIXO";
  }
  return "CREDITO NEGADO";
}

int main(void) {
  srand((unsigned)time(NULL));

  int score = rand() % 551 + 400;
  float renda = (float)(rand() % 7001 + 2000);
  float divida = (float)(rand() % 4001 + 200);

  printf("Entrada (score renda divida):\n%d %.2f %.2f\n", score, renda, divida);
  printf("Saida esperada: %s\n", classificarCredito(score, renda, divida));

  return 0;
}
