int nota = 0;
int soma = 0;
int quantidade = 0;

printf("Digite uma nota ou -1 para encerrar: ");
scanf("%d", &nota);
while (nota != -1) {
  soma += nota;
  quantidade++;
  printf("Digite uma nota ou -1 para encerrar: ");
  scanf("%d", &nota);
}
if (quantidade > 0) {
  printf("Média: %.2f\n", (float)soma / quantidade);
} else {
  printf("Nenhuma nota válida informada\n");
}
