#include <stdio.h>

void registrar_venda(void) {
  printf("Venda registrada!\n");
}

void gerar_relatorio(void) {
  printf("Relatório gerado!\n");
}

int main(void) {
  int opcao;
  do {
    printf("\n=== Menu Principal ===\n");
    printf("1 - Registrar venda\n");
    printf("2 - Gerar relatório\n");
    printf("0 - Sair\n");
    printf("Escolha: ");

    if (scanf("%d", &opcao) != 1) {
      opcao = -1;
      while (getchar() != '\n') { }
      printf("Entrada inválida. Tente novamente.\n");
      continue;
    }

    switch (opcao) {
      case 1:
        registrar_venda();
        break;
      case 2:
        gerar_relatorio();
        break;
      case 0:
        printf("Encerrando o sistema.\n");
        break;
      default:
        printf("Opção não reconhecida.\n");
    }
  } while (opcao != 0);

  return 0;
}
