#include <stdio.h>

int main(void) {
  const int linhas = 3;
  const int colunas = 4;
  int tabela[3][4];

  for (int linha = 0; linha < linhas; linha++) {
    for (int coluna = 0; coluna < colunas; coluna++) {
      tabela[linha][coluna] = (linha + 1) * (coluna + 1);
    }
  }

  for (int linha = 0; linha < linhas; linha++) {
    printf("Linha %d:", linha);
    for (int coluna = 0; coluna < colunas; coluna++) {
      printf(" %3d", tabela[linha][coluna]);
    }
    printf("\n");
  }

  return 0;
}
