int totalPedidos = 0, atrasados = 0;
int quantidade;
printf("Quantos pedidos serão processados? ");
scanf("%d", &quantidade);
if (quantidade <= 0) {
  printf("Nada a processar.\n");
  return 0;
}
for (int i = 0; i < quantidade; i++) {
  Pedido pedido = lerPedido();
  if (!pedido.valido) {
    printf("Pedido %d inválido.\n", i + 1);
    continue;
  }
  if (pedido.prioridade == ALTA) {
    trataPrioritario(pedido);
  } else if (pedido.atrasado) {
    atrasados++;
    trataAtrasado(pedido);
  } else {
    trataNormal(pedido);
  }
  totalPedidos++;
}
printf("Processados: %d | Atrasados: %d\n", totalPedidos, atrasados);

#include <stdio.h>
#include <stdlib.h>
#include <time.h>

int main(void) {
  srand((unsigned)time(NULL));

  int colaboradores = rand() % 6 + 5;

  printf("Entrada (quantidade_colaboradores):\n%d\n", colaboradores);
  printf("Saida esperada (relatorio de presenca):\n");

  for (int i = 0; i < colaboradores; ++i) {
    int horas = rand() % 49;
    int entregas = rand() % 6;
    const char *status;

    if (horas >= 40 && entregas >= 3) {
      status = "EXCELENTE";
    } else if (horas >= 30 && entregas >= 2) {
      status = "ADEQUADO";
    } else if (horas >= 20) {
      status = "ATENCAO";
    } else {
      status = "CRITICO";
    }

    printf("Colaborador %d -> horas=%d entregas=%d status=%s\n", i + 1, horas, entregas, status);
  }

  return 0;
}
