#include <stdio.h>
#include <stdlib.h>
#include <time.h>

typedef struct {
  int atendidos;
  int redirecionados;
} Turno;

int main(void) {
  srand((unsigned)time(NULL));

  Turno turnos[3];
  int totalAtendidos = 0;
  int totalRedirecionados = 0;

  for (int i = 0; i < 3; ++i) {
    turnos[i].atendidos = rand() % 41 + 30;
    turnos[i].redirecionados = rand() % 11;
    totalAtendidos += turnos[i].atendidos;
    totalRedirecionados += turnos[i].redirecionados;
  }

  printf("Relatorio consolidado de turnos:\n");
  for (int i = 0; i < 3; ++i) {
    printf("Turno %d -> atendidos=%d redirecionados=%d\n", i + 1, turnos[i].atendidos, turnos[i].redirecionados);
  }
  printf("Total atendidos: %d\nTotal redirecionados: %d\n", totalAtendidos, totalRedirecionados);

  return 0;
}
