double media(int total, int soma) {
  if (total <= 0) {
    return 0.0;
  }
  return (double)soma / total;
}

int maior(int valores[], int tamanho) {
  int max = valores[0];
  for (int i = 1; i < tamanho; i++) {
    if (valores[i] > max) {
      max = valores[i];
    }
  }
  return max;
}
