#include <stdio.h>
#include <stdlib.h>
#include <time.h>

double calcularIndiceOcupacao(int pacientes, int leitos) {
  return (double)pacientes / leitos;
}

int emitirAlerta(double indice) {
  if (indice >= 0.9) return 2;
  if (indice >= 0.75) return 1;
  return 0;
}

int main(void) {
  srand((unsigned)time(NULL));

  int leitos = rand() % 21 + 30;
  int pacientes = rand() % (leitos + 1);

  double indice = calcularIndiceOcupacao(pacientes, leitos);
  int alerta = emitirAlerta(indice);

  printf("Entrada (pacientes leitos):\n%d %d\n", pacientes, leitos);
  printf("Saidas esperadas:\n- indice_ocupacao = %.2f\n- nivel_alerta = %d\n", indice, alerta);

  return 0;
}
