#include <stdio.h>
#include <limits.h>
int main(){
  int n; if (scanf("%d", &n)!=1) return 1;
  int x, soma=0, maior=INT_MIN, menor=INT_MAX;
  for (int i=0;i<n;i++){
    scanf("%d", &x); soma+=x; if (x>maior) maior=x; if (x<menor) menor=x;
  }
  printf("soma=%d media=%.2f maior=%d menor=%d
", soma, (double)soma/n, maior, menor);
  return 0;
}
