#include <stdio.h>
#include <stdlib.h>
#include <time.h>

int main(void) {
  srand((unsigned)time(NULL));

  int linhas = 3;
  int colunas = 4;
  int matriz[3][4];

  printf("Matriz gerada:\n");
  for (int i = 0; i < linhas; ++i) {
    for (int j = 0; j < colunas; ++j) {
      matriz[i][j] = rand() % 21 + 10;
      printf("%3d ", matriz[i][j]);
    }
    printf("\n");
  }

  printf("Saida esperada (totais por coluna):\n");
  for (int j = 0; j < colunas; ++j) {
    int soma = 0;
    for (int i = 0; i < linhas; ++i) {
      soma += matriz[i][j];
    }
    printf("coluna %d -> %d\n", j + 1, soma);
  }

  return 0;
}
