void somar(int linhas, int colunas, int a[][colunas], int b[][colunas], int resultado[][colunas]) {
  for (int i = 0; i < linhas; i++) {
    for (int j = 0; j < colunas; j++) {
      resultado[i][j] = a[i][j] + b[i][j];
    }
  }
}

void transposta(int linhas, int colunas, int origem[][colunas], int destino[][linhas]) {
  for (int i = 0; i < linhas; i++) {
    for (int j = 0; j < colunas; j++) {
      destino[j][i] = origem[i][j];
    }
  }
}
