#include <stdio.h>
#include <stdlib.h>
#include <time.h>

void multiplicar(int A[2][2], int B[2][2], int C[2][2]) {
  for (int i = 0; i < 2; ++i) {
    for (int j = 0; j < 2; ++j) {
      C[i][j] = 0;
      for (int k = 0; k < 2; ++k) {
        C[i][j] += A[i][k] * B[k][j];
      }
    }
  }
}

int main(void) {
  srand((unsigned)time(NULL));

  int A[2][2];
  int B[2][2];
  int C[2][2];

  for (int i = 0; i < 2; ++i) {
    for (int j = 0; j < 2; ++j) {
      A[i][j] = rand() % 9 + 1;
      B[i][j] = rand() % 9 + 1;
    }
  }

  multiplicar(A, B, C);

  printf("Matriz A:\n%d %d\n%d %d\n", A[0][0], A[0][1], A[1][0], A[1][1]);
  printf("Matriz B:\n%d %d\n%d %d\n", B[0][0], B[0][1], B[1][0], B[1][1]);
  printf("Saida esperada (A x B):\n%d %d\n%d %d\n", C[0][0], C[0][1], C[1][0], C[1][1]);

  return 0;
}
