#include <stdio.h>
#include <stdlib.h>
#include <time.h>

typedef struct {
  int id;
  char nome[16];
  int idade;
} Paciente;

void gerarNome(char destino[], int id) {
  snprintf(destino, 16, "Paciente%02d", id);
}

int main(void) {
  srand((unsigned)time(NULL));

  Paciente p;
  p.id = rand() % 90 + 10;
  gerarNome(p.nome, p.id);
  p.idade = rand() % 60 + 18;

  printf("Registro gerado:\nID: %d\nNome: %s\nIdade: %d\n", p.id, p.nome, p.idade);
  printf("Saida esperada: atualizar CRUD em memoria com esses campos.\n");

  return 0;
}
