#include <stdio.h>
#include <stdlib.h>
#include <time.h>

typedef struct {
  int id;
  int prioridade;
  int tempoDeEspera;
} Ficha;

int main(void) {
  srand((unsigned)time(NULL));

  int quantidade = rand() % 5 + 4;
  Ficha fila[10];

  printf("Fila gerada:\n");
  for (int i = 0; i < quantidade; ++i) {
    fila[i].id = rand() % 900 + 100;
    fila[i].prioridade = rand() % 5 + 1;
    fila[i].tempoDeEspera = rand() % 41;
    printf("Ficha %d -> id=%d prioridade=%d espera=%d\n", i + 1, fila[i].id, fila[i].prioridade, fila[i].tempoDeEspera);
  }

  printf("Objetivo: ordenar por prioridade e atualizar tempo de espera acumulado.\n");

  return 0;
}
