#include <stdio.h>

int main() {
    // Input variable
    float salario_base;

    // Processing variables
    float gratificacao, imposto, salario_liquido;

    // 1. INPUT
    printf("Digite o salario-base do funcionario: R$ ");
    scanf("%f", &salario_base);

    // 2. PROCESSING
    gratificacao = salario_base * 0.05f; // 5% bonus
    imposto = salario_base * 0.07f;      // 7% tax
    salario_liquido = salario_base + gratificacao - imposto;

    // 3. OUTPUT
    printf("\n--- Holerite ---\n");
    printf("Salario Base: R$ %10.2f\n", salario_base);
    printf("Gratificacao: R$ %10.2f\n", gratificacao);
    printf("Imposto:      R$ %10.2f\n", imposto);
    printf("--------------------------\n");
    printf("Salario Liquido: R$ %8.2f\n", salario_liquido);

    return 0;
}

#include <stdio.h>

int main() {
    // Input variables
    float peso, altura;

    // Processing variable
    float imc;

    // 1. INPUT
    printf("Digite seu peso (em kg): ");
    scanf("%f", &peso);
    printf("Digite sua altura (em metros): ");
    scanf("%f", &altura);

    // 2. PROCESSING
    imc = peso / (altura * altura);

    // 3. OUTPUT
    printf("\n--- Resultado do IMC ---\n");
    printf("Peso: %.2f kg\n", peso);
    printf("Altura: %.2f m\n", altura);
    printf("Seu IMC e: %.2f\n", imc);

    return 0;
}
