fun openWebsite(url: String) {
  val intent = Intent(Intent.ACTION_VIEW, Uri.parse(url))
  startActivity(Intent.createChooser(intent, "Abrir com"))
}

fun dialPhone(phone: String) {
  val intent = Intent(Intent.ACTION_DIAL).apply {
    data = Uri.parse("tel:$phone")
  }
  startActivity(intent)
}

fun shareText(message: String) {
  val intent = Intent(Intent.ACTION_SEND).apply {
    type = "text/plain"
    putExtra(Intent.EXTRA_TEXT, message)
  }
  startActivity(Intent.createChooser(intent, "Compartilhar via"))
}

fun Intent.safeStart(context: Context, onUnavailable: () -> Unit) {
  if (resolveActivity(context.packageManager) != null) {
    context.startActivity(this)
  } else {
    onUnavailable()
  }
}

fun shareImage(uri: Uri) {
  val intent = Intent(Intent.ACTION_SEND).apply {
    type = "image/png"
    putExtra(Intent.EXTRA_STREAM, uri)
    addFlags(Intent.FLAG_GRANT_READ_URI_PERMISSION)
  }
  intent.safeStart(this) {
    showSnackbar("Nenhum aplicativo compatível encontrado")
  }
}
