package br.com.unichristus.ads.sharedprefs

import android.content.SharedPreferences
import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import android.widget.Button
import android.widget.EditText
import android.widget.TextView

class MainActivity : AppCompatActivity() {

    private lateinit var textViewSaudacao: TextView
    private lateinit var editTextNome: EditText
    private lateinit var buttonSalvar: Button
    private lateinit var sharedPreferences: SharedPreferences

    companion object {
        private const val ARQUIVO_PREFS = "AppPrefs"
        private const val CHAVE_NOME = "NOME_USUARIO"
    }

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_main)

        textViewSaudacao = findViewById(R.id.textViewSaudacao)
        editTextNome = findViewById(R.id.editTextNome)
        buttonSalvar = findViewById(R.id.buttonSalvar)

        sharedPreferences = getSharedPreferences(ARQUIVO_PREFS, MODE_PRIVATE)

        carregarSaudacao()

        buttonSalvar.setOnClickListener {
            salvarNome()
        }
    }

    private fun carregarSaudacao() {
        val nomeSalvo = sharedPreferences.getString(CHAVE_NOME, null)
        if (nomeSalvo != null && nomeSalvo.isNotBlank()) {
            textViewSaudacao.text = "Olá, $nomeSalvo!"
        } else {
            textViewSaudacao.text = "Olá, Visitante!"
        }
    }

    private fun salvarNome() {
        val nomeDigitado = editTextNome.text.toString()
        if (nomeDigitado.isNotBlank()) {
            val editor = sharedPreferences.edit()
            editor.putString(CHAVE_NOME, nomeDigitado)
            editor.apply()

            textViewSaudacao.text = "Olá, $nomeDigitado!"
            editTextNome.text.clear()
        }
    }
}

