class CatalogoViewModel(private val repository: ProdutoRepository) : ViewModel() {
    val produtos: LiveData<List<Produto>> = repository.todos().asLiveData()

    private val _mensagem = MutableLiveData<Event<String>>()
    val mensagem: LiveData<Event<String>> = _mensagem

    fun remover(produto: Produto) {
        viewModelScope.launch {
            repository.remover(produto)
            _mensagem.postValue(Event("Produto removido com sucesso!"))
        }
    }
}

