@Entity(tableName = "registros")
data class Registro(
    @PrimaryKey(autoGenerate = true) val id: Long = 0,
    val titulo: String,
    val atualizadoEm: Long
)

@Dao
interface RegistroDao {
    @Query("SELECT * FROM registros ORDER BY atualizadoEm DESC")
    fun listar(): Flow<List<Registro>>
}
