val db = Firebase.firestore
val usuarios = db.collection("usuarios")
usuarios.whereEqualTo("ativo", true)
    .addSnapshotListener { snapshot, erro ->
        if (erro != null) {
            Log.e("UnidadeV", "Erro ao consultar Firestore: modelagem e operações", erro)
            return@addSnapshotListener
        }
        val ativos = snapshot?.documents?.size ?: 0
        Log.d("UnidadeV", "Usuários ativos: $ativos")
    }
