// Modern and concise lambda syntax (EN)
meuButton.setOnClickListener {
    // Action to be executed when the button is clicked
    Log.d(TAG, "Botão foi clicado!")
}

// Legacy anonymous class syntax (EN)
meuButton.setOnClickListener(object : View.OnClickListener {
    override fun onClick(v: View?) {
        // Action to be executed
        Log.d(TAG, "Botão foi clicado!")
    }
})

// Long click example (EN)
meuButton.setOnLongClickListener {
    Log.d(TAG, "O botão foi pressionado por um tempo!")
    true // Returning 'true' indicates the event was consumed
}

package br.com.unichristus.ads.olamundoandroid

import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import android.util.Log
import android.widget.Button
import android.widget.EditText

class MainActivity : AppCompatActivity() {

    // Good practice (EN): Define a constant TAG for logs from this class
    private val TAG = "MainActivity"

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_main)

        // Find views by ID defined in XML
        val meuButton: Button = findViewById(R.id.buttonSaudacao)
        val meuEditText: EditText = findViewById(R.id.editTextNome)

        // Set click listener for the button (lambda syntax)
        meuButton.setOnClickListener {
            // This block runs when the button is clicked

            // 1) Read the text typed by the user
            val nomeDigitado = meuEditText.text.toString()

            // 2) Show the text in Logcat using Debug level
            Log.d(TAG, "O nome digitado foi: $nomeDigitado")
        }
    }
}
