package edu.lpoo.legacy;

import java.time.LocalDate;
import java.util.ArrayList;
import java.util.List;

public class EnrollmentForm {
    // PROBLEMA: campos expostos quebram o encapsulamento
    public String studentName;
    public LocalDate birthDate;
    public List<String> selectedModules = new ArrayList<>();
    public ContactInfo emergencyContact;

    public EnrollmentForm(String studentName, LocalDate birthDate, ContactInfo emergencyContact) {
        // PROBLEMA: nenhum tipo de validação ou normalização da entrada
        this.studentName = studentName;
        this.birthDate = birthDate;
        this.emergencyContact = emergencyContact;
    }

    public List<String> getSelectedModules() {
        // PROBLEMA: devolve referência mutável interna
        return selectedModules;
    }

    public void replaceEmergencyContact(ContactInfo emergencyContact) {
        // PROBLEMA: aceita valores inválidos sem lançar exceções
        this.emergencyContact = emergencyContact;
    }

    public void syncFrom(EnrollmentForm other) {
        // PROBLEMA: cópia rasa quebra invariantes e imutabilidade desejada
        this.selectedModules = other.selectedModules;
        this.emergencyContact = other.emergencyContact;
    }
}

class ContactInfo {
    // PROBLEMA: estrutura auxiliar também expõe estado mutável
    public String name;
    public String phone;
    public List<String> extraPhones = new ArrayList<>();
}
