public InputAction playerAction;

public void StartRebinding() {
    playerAction.Disable();
    var rebindOperation = playerAction.PerformInteractiveRebinding()
        .WithControlsExcluding("<Mouse>/position")
        .OnComplete(operation => {
            playerAction.Enable();
            // Salve o override do binding
            string bindingOverride = playerAction.SaveBindingOverridesAsJson();
            PlayerPrefs.SetString("binding", bindingOverride);
            PlayerPrefs.Save();
            operation.Dispose();
        })
        .Start();
}

public TextMeshProUGUI subtitleText;

public void ToggleSubtitles(bool isOn) {
    subtitleText.gameObject.SetActive(isOn);
    PlayerPrefs.SetInt("subtitles_enabled", isOn ? 1 : 0);
}

public void SetSubtitleSize(float newSize) {
    subtitleText.fontSize = newSize;
    PlayerPrefs.SetFloat("subtitle_size", newSize);
}

public Material highContrastMaterial;
private MeshRenderer objectRenderer;

public void SetHighContrast(bool isHighContrast) {
    if (isHighContrast) {
        objectRenderer.material = highContrastMaterial;
    } else {
        // Retorna ao material original
    }
}

public AudioMixer masterMixer;

public void SetMusicVolume(float volume) {
    // 'MusicVolume' deve ser um parâmetro exposto no Audio Mixer
    // O volume é em dB, então convertemos de um slider (0-1) para uma escala logarítmica
    masterMixer.SetFloat("MusicVolume", Mathf.Log10(volume) * 20);
    PlayerPrefs.SetFloat("music_volume", volume);
}

[CreateAssetMenu(fileName = "DifficultyPreset", menuName = "Game/Difficulty Preset")]
public class DifficultyPreset : ScriptableObject {
    public float enemyDamageMultiplier = 1f;
    public float playerHealthMultiplier = 1f;
    public bool aimAssist = false;
}

// Em outro script, você pode ter uma referência ao preset atual
public DifficultyPreset currentDifficulty;

void ApplyDifficulty() {
    float finalDamage = baseDamage * currentDifficulty.enemyDamageMultiplier;
}
